/**
 *
Longest Substring Without Repeating Characters
Medium

Given a string, find the length of the longest substring without repeating
characters.

Example 1:
Input: "abcabcbb"
Output: 3
Explanation: The answer is "abc", with the length of 3.

Example 2:
Input: "bbbbb"
Output: 1
Explanation: The answer is "b", with the length of 1.

Example 3:
Input: "pwwkew"
Output: 3
Explanation: The answer is "wke", with the length of 3.
Note that the answer must be a substring, "pwke" is a subsequence and not a
substring.
 */
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#define DEFAULT 512
typedef struct _entry *Entry;
struct _entry {
  char key;
  int value;
  Entry next;
};
typedef struct _map *HashMap;
struct _map {
  int size;      // 标识当前map元素个数
  int usage;     // 标识数组已使用个数
  int length;    // 标识数组长度
  Entry *datas;  // 数据
};

Entry makeEntry(char s, int value) {
  Entry entry = malloc(sizeof *entry);
  entry->key = s;
  entry->value = value;
  entry->next = NULL;
  return entry;
}
HashMap initHashMap() {
  HashMap map = malloc(sizeof *map);
  map->size = 0;
  map->usage = 0;
  map->length = DEFAULT;
  map->datas = (Entry *)malloc(sizeof(Entry) * map->length);
  for (int i = 0; i < map->length; i++) {
    map->datas[i] = NULL;
  }
  return map;
}
Entry findEntry(Entry entry, char key) {
  if (!entry) return NULL;
  Entry current = entry;
  while (current && current->key != key) {
    current = current->next;
  }
  return current;
}
int findPosition(HashMap map, char key) {
  if (!map) return -1;
  int hash = key;
  if (hash < 0) hash = -hash;
  int pos = hash % map->length;
  return pos;
}
void putToHashMap(HashMap map, char key, int value) {
  if (!map) return;
  int pos = findPosition(map, key);
  Entry entry = makeEntry(key, value);
  if (map->datas[pos] == NULL) {
    map->datas[pos] = entry;
    map->usage += 1;
    map->size += 1;
  } else {
    // 这里需要检查key值是否已存在，如果已存在，那么需要覆盖当前的值
    Entry existEntry = findEntry(map->datas[pos], key);
    if (existEntry) {
      // 如果key已存在，直接覆盖value
      existEntry->value = value;
    } else {
      // 如果不存在该key，则将该节点插入到链表
      entry->next = map->datas[pos];
      map->datas[pos] = entry;
      map->size += 1;
    }
  }
}

int getFromHashMap(HashMap map, char key) {
  if (!map) return -1;
  int pos = findPosition(map, key);
  Entry start = map->datas[pos];
  if (start == NULL) return -1;
  while (start->key != key && start->next != NULL) {
    start = start->next;
  }
  if (start->key == key) return start->value;
  return -1;
}

void clearHashMap(HashMap map) {
  for (int i=0;i<map->length;i++) {
    Entry entry = map->datas[i];
    while (entry) {
      Entry e = entry;
      entry = entry->next;
      free(e);
    }
    map->datas[i] = NULL;
  }

  map->size = 0;
  map->usage = 0;

}


/**
 * 最简单直接的一个方法就是，从第一个字符开始，依次遍历字符串。
 * 将每个字符放到HashMap中，如果该字符在之前的位置已出现过，计算长度，并判断该长度是否是最大长度
 */
int lengthOfLongestSubstring(char *s) {
  int length = strlen(s);
  if (length < 2) return length;
  int max = 0;
  int j = 0;
  HashMap map = initHashMap();
  for (int i = 0; i < length; i++) {
    clearHashMap(map);
    putToHashMap(map, s[i], 1);
    for (j = i+1; j < length; j++) {
      char c = s[j];
      if (getFromHashMap(map, c) != -1) {
        break;
      }
      putToHashMap(map, c, 1);
    }
    if (j-i>max) {
      max = j-i;
    }
  }
  return max;
}

/**
 * 上面第一种方法，最简单直接，也最好理解。但效率却不怎么高。为啥呢？
 * 试想一下如下字符串：
 * abcdefgbdf
 * 其中最长的字串应该是 abcdefg 
 * 但这其中其实重复算了好几遍，为啥呢？
 * 比如，开始从a开始的时候， abcdefg 算了一遍
 * 从b开始时， bcdefg 又算了一遍，虽然不是最长的字串，但是确实在计算过程中算了其长度。
 * 从c开始时， cdefg 又算了一遍。
 * 因此，分析下来，这个字串其实多算了 6 遍。从优化的角度看，这些其实是可以直接略过的。
 * 其实我们可以只需要遍历一遍，在遍历时记录最长的字串即可。
 * 比如字符串：abcdefaojklmnqpeoak
 * 当我们遍历到第6个字符（a）时，由于a已重复了，我们能拿到此时最长子串为 abcdef
 * 当我们继续遍历到第15个字符(e)时，由于e重复了，这时最长子串为 bcdefaojklmnqp
 * 这时由于e重复了，我们计算最长子串的开始应该从第5个字符（f）开始，为啥呢，因为重复的e(第4个字符)是在最长字串 bcdefaojklmnqp 里
 * 如果从第2个字符开始算，其实中间又重复算了很多不必要的字串，因为这些字串都不如 bcdefaojklmnqp 长
 * 
 */
int lengthOfLongestSubstring2(char* s) {
  int length = strlen(s);
  if (length<2) {
    return length;
  }
  int start = 0;
  int end = 0;
  int max = 0;
  HashMap map = initHashMap();

  while(end < length) {
    char c = s[end];
    if (getFromHashMap(map, c) >= start) {
      if (end - start > max) max = end - start;
      start = getFromHashMap(map, c) + 1;
    }
    putToHashMap(map, c, end);
    end += 1;
  }
  if (end - start > max) max = end - start;
  return max;
}

