// @lc code=start

func threeSum(nums []int) [][]int {
	result := [][]int{}
	twoSum := []int{}
	ns := []int{}
	numCount := map[int]int{}
	for _, n := range nums {
		numCount[n]++
		if numCount[n] <= 3 {
			ns = append(ns, n)
		}
	}
	nums = ns
	for _, n := range nums {
		twoSum = append(twoSum, 0-n)
	}
	exist := map[string]bool{}
	// 以twoSum的思路解决
	for i := 0; i < len(twoSum); i++ {
		sum := twoSum[i]
		subs := map[int]bool{}
		for j := 0; j < len(nums); j++ {
			if i == j {
				continue
			}
			sub := sum - nums[j]
			if subs[nums[j]] {
				keyItems := []int{nums[i], sub, nums[j]}
				sort.Ints(keyItems)
				key := fmt.Sprintf("%d+%d+%d", keyItems[0], keyItems[1], keyItems[2])
				if !exist[key] {
					result = append(result, keyItems)
					exist[key] = true
				}
			} else {
				subs[sub] = true
			}
		}
	}
	return result
}

// @lc code=end
